mapper.select:
select id, city, station, building_id as buildingId, `time`, time_prefix as timePrefix, heat_supply as heatSupply
from building_supply
where id = #{id};

select id, city, station, building_id as buildingId, `time`, time_prefix as timePrefix, heat_supply as heatSupply
from building_supply
where building_id = #{buildingId} and TO_DAYS(`time`) = TO_DAYS(#{day,jdbcType=DATE});
